<?phpdefine('THEME_NAME','iphoto');load_theme_textdomain( THEME_NAME,TEMPLATEPATH .'/languages');add_custom_background();add_theme_support( 'automatic-feed-links' );add_theme_support( 'post-formats', array( 'video'));if ( function_exists('register_nav_menus') ) {	register_nav_menus(array('primary' => 'header'));}add_action('wp_ajax_b_ajax_post_action', 'b_ajax_callback');function b_ajax_callback() {  global $wpdb;  if(isset($_POST['type']) && $_POST['type'] == 'upload') {    $clickedID = $_POST['data'];     $filename = $_FILES[$clickedID];    $filename['name'] = preg_replace('/[^a-zA-Z0-9._-]/', '', $filename['name']);     $override['test_form'] = false;    $override['action'] = 'wp_handle_upload';        $uploaded_file = wp_handle_upload($filename,$override);    $upload_tracking[] = $clickedID;	    update_option($clickedID, $uploaded_file['url'] );			    if(!empty($uploaded_file['error'])) {echo 'Upload Error: ' . $uploaded_file['error']; }	    else { echo $uploaded_file['url']; }  }	  die();}function post_thumbnail(){	global $post;	$link = get_permalink($post->ID);	$title = $post->post_title;	$post_img = '';	ob_start();	ob_end_clean();	$output = preg_match_all('/\<img.+?src="(.+?)".*?\/>/is',$post->post_content,$matches ,PREG_SET_ORDER);	$cnt = count( $matches );	if($cnt>0){		$post_img_src = $matches [0][1];		$post_img = '<a class="inimg" href="'.$link.'" title="'.$title.'"><img src="'.get_bloginfo('template_url').'/timthumb.php?src='.$post_img_src.'&amp;w=285&amp;zc=1" alt="'.$title.'"/></a>';		echo $post_img;	}else{		echo '<a class="noimg" href="'.$link.'" title="'.$title.'">'.$title.'</a><p class="noimg">'.mb_strimwidth(strip_tags(apply_filters('the_content', $post->post_content)), 0, 200,"...").'</p>';	}}function ajax_post(){	if( isset($_GET['action'])&& $_GET['action'] == 'ajax_post'){		if(isset($_GET['meta'])){			$args = array(				'meta_key' => $_GET['meta'],				'orderby'   => 'meta_value_num',				'paged' => $_GET['pag'],				'order' => DESC			);		}else if(isset($_GET['cat'])){			$args = array(				'category_name' => $_GET['cat'],				'paged' => $_GET['pag']			);		}else if(isset($_GET['pag'])){			$args = array(				'paged' => $_GET['pag']			);		}		query_posts($args);		if(have_posts()){while (have_posts()):the_post();?>					<?php get_template_part( 'content', get_post_format() ); ?>			<?php endwhile;}		die();	}else{return;}}add_action('init', 'ajax_post');function pagenavi( $p = 2 ) {if ( is_singular() ) return;global $wp_query,$paged;$max_page = $wp_query->max_num_pages;if ( $max_page == 1 ){echo '<span id="post-current">1</span> / <span id="post-count">1</span>'; return;}if ( empty( $paged ) ) $paged = 1;if ( $paged >1 ) echo "<a id='prev' title='Prev' href='",esc_html( get_pagenum_link( $paged -1 ) ),"'>&lt;&lt;</a> ";echo '<span id="post-current">'.$paged .'</span> / <span id="post-count">'.$max_page .'</span>';if ( $paged <$max_page  ) echo "<a id='next' title='Next' href='",esc_html( get_pagenum_link( $paged +1) ),"'>&gt;&gt;</a> ";}function iphoto_comment($comment,$args,$depth) {$GLOBALS['comment'] = $comment;;echo '	<li ';comment_class();;echo ' id="li-comment-';comment_ID() ;echo '" >		<div id="comment-';comment_ID();;echo '" class="comment-body">			<div class="commentmeta">';echo get_avatar( $comment->comment_author_email,$size = '48');;echo '</div>				';if ($comment->comment_approved == '0') : ;echo '				<em>';_e('Comment của bạn chờ quản trị xét duyệt trước khi đăng.') ;echo '</em><br />				';endif;;echo '			<div class="commentmetadata">&nbsp;-&nbsp;';printf(__('%1$s %2$s'),get_comment_date('Y.n.d'),get_comment_time('G:i'));;echo '</div>			<div class="reply">';comment_reply_link(array_merge( $args,array('depth'=>$depth,'max_depth'=>$args['max_depth'],'reply_text'=>__('Reply')))) ;echo '</div>			<div class="vcard">';printf(__('%s'),get_comment_author_link()) ;echo '</div>			';comment_text() ;echo '		</div>';}add_action('admin_init', 'iphoto_init');function iphoto_init() {	if (isset($_GET['page']) && $_GET['page'] == 'functions.php') {		$dir = get_bloginfo('template_directory');		wp_enqueue_script('adminjquery', $dir . '/includes/admin.js', false, '1.0.0', false);		wp_enqueue_style('admincss', $dir . '/includes/admin.css', false, '1.0.0', 'screen');	}}add_action('admin_menu','iphoto_page');function iphoto_page (){if ( count($_POST) >0 &&isset($_POST['iphoto_settings']) ){$options = array ('keywords','description','analytics','lib','views','noajax','animate','phzoom','copyright');foreach ( $options as $opt ){delete_option ( 'iphoto_'.$opt,$_POST[$opt] );add_option ( 'iphoto_'.$opt,$_POST[$opt] );}}add_theme_page('iPhoto '.__('Theme Options',THEME_NAME),__('Theme Options',THEME_NAME),'edit_themes',basename(__FILE__),'iphoto_settings');}function iphoto_settings(){?><div class="wrap"><div><h2><?php _e( 'Tùy chỉnh iPhoto Theme<span>Phiên bản: ',THEME_NAME);?><?php $theme_data=get_theme_data(TEMPLATEPATH . '/style.css'); echo $theme_data['Version'];?></span></h2></div><div class="clear"></div><form method="post" action="">	<div id="theme-Option">		<div id="theme-menu">			<span class="m1"><?php _e( 'Hiệu ứng jQuery',THEME_NAME);?></span>			<span class="m2"><?php _e( 'Tương thích Plugins',THEME_NAME);?></span>			<span class="m3"><?php _e( 'Thông tin website',THEME_NAME);?></span>			<span class="m4"><?php _e( 'Analytics Code',THEME_NAME);?></span>			<span class="m5"><?php _e( 'bản quyền dưới Footer',THEME_NAME);?></span>			<span class="m6"><?php _e( 'Thông tin Theme',THEME_NAME);?></span>			<div class="clear"></div>		</div>		<div id="theme-content">			<ul>				<li>				<tr><td>					<em><?php _e( 'iPhoto sữ dụng thư viện jQuery 1.4.4. bạn có thể sữ dụng thư viện tương tự tại thư viện jQuery của tôi tại google hay thư viện khác',THEME_NAME);?></em><br/>					<label><input name="lib" type="checkbox" id="lib" value="1" <?php if (get_option('iphoto_lib')!='') echo 'checked="checked"' ;?>/><?php _e( 'Load thư viện jQuery của Google',THEME_NAME);?></label><br/><br/>				</td></tr>				<tr><td>					<em><?php _e( 'Load bài viết của index trên home một cách vô hạn ( Không giới hạn ảnh ).',THEME_NAME);?></em><br/>					<label><input name="noajax" type="checkbox" id="noajax" value="1" <?php if (get_option('iphoto_noajax')!='') echo 'checked="checked"' ;?>/><?php _e( 'Vô hiệu hóa tính năng load vô hạn',THEME_NAME);?></label><br/><br/>				</td></tr>				<tr><td>					<em><?php _e( '<strong>Bố trí lại các Layout động</strong>',THEME_NAME);?></em><br />					<input name="animate" type="checkbox" id="animate" value="1" <?php if (get_option('iphoto_animate')!='') echo 'checked="checked"';?>/><?php _e( 'Vô hiệu hóa tính năng tải ảnh dạng động trên trang chủ',THEME_NAME);?>				</td></tr>				</li>				<li>				<tr><td>					<em><?php _e( 'WP-PostViews, Bạn kích hoạt tính năng này để xem số lượng xem bài hay trang của bạn.',THEME_NAME);?></em><br/>					<label><input name="views" type="checkbox" id="views" value="1" <?php if (get_option('iphoto_views')!='') echo 'checked="checked"' ?>/><?php _e( 'Kích hoạt WP-PostViews',THEME_NAME);?></label><br/><br/>				</td></tr>				</li>				<li>							<tr><td>				<?php _e( '<em>Keywords, separate by English commas. like httmedia, Computer, Software</em>',THEME_NAME);?><br/>				<textarea name="keywords" id="keywords" rows="1" cols="70" style="font-size:11px;width:100%;"><?php echo get_option('iphoto_keywords');?></textarea><br/>				</td></tr>			<tr><td>				<?php _e( '<em>Description, explain what\'s this site about. like httmedia, Breathing the wind</em>',THEME_NAME);?><br/>				<textarea name="description" id="description" rows="3" cols="70" style="font-size:11px;width:100%;"><?php echo get_option('iphoto_description');?></textarea>					</td></tr>				</li>				<li>							<tr><td>				<?php _e( 'Bạn có thể tạo Google Analytics code <a target="_blank" href="https://www.google.com/analytics/settings/check_status_profile_handler">Tại đây</a>.',THEME_NAME);?></label><br>				<textarea name="analytics" id="analytics" rows="5" cols="70" style="font-size:11px;width:100%;"><?php echo stripslashes(get_option('iphoto_analytics'));?></textarea>			</td></tr>				</li>				<li>							<tr><td>				<textarea name="copyright" id="copyright" rows="5" cols="70" style="font-size:11px;width:100%;"><?php if(stripslashes(get_option('iphoto_copyright'))!=''){echo stripslashes(get_option('iphoto_copyright'));}else{echo 'Copyright &copy; '.date('Y').' '.'<a href="'.home_url( '/').'" title="'.esc_attr( get_bloginfo( 'name') ).'">'.esc_attr( get_bloginfo( 'name') ).'</a> All rights reserved'; };?></textarea>				<br/><em><?php _e( '<b>Preview</b>',THEME_NAME);?><span> : </span><span><?php if(stripslashes(get_option('iphoto_copyright'))!=''){echo stripslashes(get_option('iphoto_copyright'));}else{echo 'Copyright &copy; '.date('Y').' '.'<a href="'.home_url( '/').'" title="'.esc_attr( get_bloginfo( 'name') ).'">'.esc_attr( get_bloginfo( 'name') ).'</a> All rights reserved'; };?></span></em>			</td></tr>				</li>				<li>							<tr><td>			<p><?php _e('iPhoto được tạo và phát triển bởi  <a href="http://httmedia.com/">httmedia</a>.  nếu bạn thích theme iphoto này , bạn có thể ủng hộ tôi để có đủ kinh phí những phát triển mới . Ủng hộ qua ',THEME_NAME);?><?php _e('Paypal',THEME_NAME);?>:</strong> <a href="https://www.paypal.com/cgi-bin/webscr?cmd=_donations&business=UNZTBMM2W4AHA&lc=VN&item_name=HTTMEDIA&currency_code=USD&bn=PP%2dDonationsBF%3abtn_donateCC_LG%2egif%3aNonHosted" target="_blank" title="HTTVN">hungtrungthinhvn@gmail.com</a></p>			<h3 style="color:#333" id="introduce"><?php _e( 'Introduction',THEME_NAME);?></h3>			<p style="text-indent: 2em;margin:10px 0;"><?php _e( 'iPhoto được phát triển từ một chủ đề của Tumblr và biến nó thành một chủ đề ảnh có thể được sử dụng tại wordpress.',THEME_NAME);?></p>			<h3 style="color:#333"><?php _e( 'Published Address',THEME_NAME);?></h3>			<p  id="release" style="text-indent: 2em;margin:10px 0;"><a href="http://httmedia.com/iphoto" target="_blank">http://httmedia.com</a></p>			<h3 style="color:#333"><?php _e( 'Preview Address',THEME_NAME);?></h3>			<p  id="preview" style="text-indent: 2em;margin:10px 0;"><a href="http://httmedia.com/iphoto/" target="_blank">http://httmedia.com/iphoto/</a></p>			<h3 style="color:#333" id="bug"><?php _e( 'Report Bugs',THEME_NAME);?></h3>			<p style="text-indent: 2em;margin:10px 0;"><?php _e( 'HTTVN <a href="http://httmedia.com" target="_blank">@httmedia.com</a> or leave a message at <a href="http://httmedia.me" target="_blank">http://httmedia.me</a>。',THEME_NAME);?></p>			</td></tr>				</li>			</ul>		</div>	</div>	<p class="submit">		<input type="submit" name="Submit" class="button-primary" value="<?php _e( 'Save Options',THEME_NAME);?>" />		<input type="hidden" name="iphoto_settings" value="save" style="display:none;" />	</p></form></div><?php}?>